mapper.select:
SELECT count(distinct nbxh) FROM bbd_company_remind_detail
WHERE `send_year`= #{year} and `type` = #{type}

    and district = #{district}

SELECT count(distinct nbxh) FROM bbd_company_remind_detail
WHERE `send_year`= #{year} and `send_month` = #{month} and `type` = #{type}

    and district = #{district}
