mapper.update:
update bbd_company_remind_statistics

    set mail_success = mail_success + #{num}


    set msg_success = msg_success + #{num}


    set voice_success = voice_success + #{num}

where nbxh = #{nbxh} and send_year = #{sendYear}

update bbd_company_remind_statistics

    set mail_fail = mail_fail + #{num}


    set msg_fail = msg_fail + #{num}


    set voice_fail = voice_fail + #{num}

where nbxh = #{nbxh} and send_year = #{sendYear}
