mapper.select:
SELECT COUNT(nbxh) FROM bbd_enterprise_info WHERE abnormal_state > 0
AND district = #{district}
AND name_type IN ('98', '06')

select count(nbxh) from bbd_enterprise_info where (abnormal_state div #{abnormalState}) mod 2 = 1
and district = #{district}
and name_type in ('98', '06')

SELECT primary_industry_initial as name, COUNT(1) as value FROM bbd_enterprise_info WHERE (abnormal_state div #{abnormalState}) mod 2 = 1 AND primary_industry_initial IS NOT NULL
AND district = #{district}
AND name_type IN ('98', '06') GROUP BY primary_industry_initial ORDER BY COUNT(1) DESC

SELECT company_property as name, COUNT(1) as value FROM bbd_enterprise_info WHERE (abnormal_state div #{abnormalState}) mod 2 = 1
AND district = #{district}
AND name_type IN ('98', '06') GROUP BY company_property ORDER BY COUNT(1) DESC

SELECT COUNT(1) FROM bbd_abnormal_info a, bbd_enterprise_info c WHERE a.nbxh=c.nbxh AND YEAR(gmt_delete) = #{year}
and c.district = #{district}
and (c.abnormal_state div #{abnormalState}) mod 2 = 1
AND c.name_type IN ('98', '06')

SELECT COUNT(1) FROM bbd_abnormal_info a, bbd_enterprise_info c
WHERE a.nbxh=c.nbxh AND YEAR(gmt_delete) = #{year} AND MONTH(gmt_delete)=#{month}

    and c.district = #{district}


    and (c.abnormal_state div #{abnormalState}) mod 2 = 1

AND c.name_type IN ('98', '06')

SELECT COUNT(1) FROM bbd_abnormal_info a, bbd_enterprise_info c
WHERE a.nbxh=c.nbxh AND YEAR(gmt_checkin) = #{year} AND MONTH(gmt_checkin)=#{month}
AND a.delete_flag = 0

    and c.district = #{district}


    and (c.abnormal_state div #{abnormalState}) mod 2 = 1

AND c.name_type IN ('98', '06')
