mapper.select:
SELECT COUNT(1) FROM bbd_annual_info a, bbd_enterprise_info c
WHERE a.nbxh = c.nbxh

    and c.district = #{district}

AND YEAR(a.gmt_annual)=#{year} AND MONTH(a.gmt_annual) = #{month}

SELECT primary_industry_initial as name, COUNT(1) as value FROM bbd_enterprise_info WHERE annual_state = 2 AND primary_industry_initial IS NOT NULL
and district = #{district}
GROUP BY primary_industry_initial ORDER BY COUNT(1) DESC

SELECT company_property as name, COUNT(1) as value FROM bbd_enterprise_info WHERE annual_state = 2
AND district = #{district}
GROUP BY company_property ORDER BY COUNT(1) DESC
